document.addEventListener("DOMContentLoaded",()=>{const lazyImages=document.querySelectorAll("img.lazyload");const lazyLoad=(entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;if(img.dataset.src){img.src=img.dataset.src}img.classList.remove("lazyload");observer.unobserve(img)}})};if("IntersectionObserver"in window){const observer=new IntersectionObserver(lazyLoad,{rootMargin:"0px 0px 100px 0px"});lazyImages.forEach(img=>{observer.observe(img)})}else{lazyImages.forEach(img=>{if(img.dataset.src){img.src=img.dataset.src}img.classList.remove("lazyload")})}const readMoreButtons=document.querySelectorAll(".read-more-btn");readMoreButtons.forEach(button=>{button.addEventListener("click",e=>{e.preventDefault();const postContentWrapper=button.previousElementSibling;if(postContentWrapper){postContentWrapper.classList.toggle("expanded");if(postContentWrapper.classList.contains("expanded")){button.textContent="Read Less"}else{button.textContent="Read More"}}})});const contactForm=document.getElementById("contact-form");if(contactForm){contactForm.addEventListener("submit",e=>{e.preventDefault();const formData=new FormData(contactForm);const formObject={};formData.forEach((value,key)=>{formObject[key]=value});console.log("Form Submitted:",formObject);alert("Thank you for your message! We will get back to you soon.");contactForm.reset()})}document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener("click",function(e){e.preventDefault();const targetId=this.getAttribute("href").substring(1);const targetElement=document.getElementById(targetId);if(targetElement){targetElement.scrollIntoView({behavior:"smooth"})}})})});